import React from "react";
import "./App.css";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";

// importamos las paginas
import LoginPage from "./pages/LoginPage";
import HomePage from "./pages/HomePage";
import RegisterPage from "./pages/RegisterPage";
import EmpleadoPage from "./pages/EmpleadoPage";
import TarjetaUsuarioPage from "./pages/TarjetaUsuarioPage";
import ServicioUsuarioPage from "./pages/ServicioUsuarioPage";
import PagoPage from "./pages/PagoPage";

//importamos los componentes
import Footer from "./components/Footer";

//importamos en index.js bootstrap

//importamos el estilo
import "./pages/style.scss";

//npm run start
//npm install --save react-router-dom
//npm install --save react-bootstrap bootstrap
//npm install --save bootstrap
//npm install --save node-sass

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogginActive: true,
    };
  }

  render() {
    const { isLogginActive } = this.state;
    return (
      <Router>
        <Container className="p-0 Nav" fluid={true}>
          <Navbar className="border-bottom" bg="transparent" expand="lg">
            <Navbar.Brand>MANDE</Navbar.Brand>
            <Navbar.Toggle
              className="border-0"
              aria-controls="navbar-toogle"
            ></Navbar.Toggle>
            <Navbar.Collapse id="navbar-toogle">
              <Nav className="ml-auto">
                <Link className="nav-link" to="/">
                  Home
                </Link>
                <Link className="nav-link" to="/login">
                  Login
                </Link>
                <Link className="nav-link" to="/register">
                  Register
                </Link>
                <Link className="nav-link" to="/nuevoEmpleado">
                  Empleado
                </Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>

          {/* Router HomePage */}

          <Route path="/" exact>
            <HomePage />
          </Route>

          {/* Router LoginPage */}

          <Route path="/login">
            <LoginPage />
          </Route>

          {/* Router RegisterPage */}

          <Route path="/register">
            <RegisterPage />
          </Route>

          {/* Router EmpleadoPage */}

          <Route path="/nuevoEmpleado">
            <EmpleadoPage />
          </Route>

          <Footer />
        </Container>
      </Router>
    );
  }
}

export default App;
